  }
  nextServicePage() {
    this.goToServicePage(this.serviceCurrentPage + 1);
  }
  // Funciones de navegación de páginas para usuarios
  goToUserPage(page) {
    if (page >= 1 && page <= this.userTotalPages) {
      this.userCurrentPage = page;
      this.updateUserPagination();
